/*
 * var_x400pMIB():
 *   This function is called every time the agent gets a request for
 *   a scalar variable that might be found within your mib section
 *   registered above.  It is up to you to do the right thing and
 *   return the correct value.
 *     You should also correct the value of "var_len" if necessary.
 *
 *   Please see the documentation for more information about writing
 *   module extensions, and check out the examples in the examples
 *   and mibII directories.
 */
u_char *
var_x400pMIB(struct variable *vp, oid * name, size_t *length, int exact, size_t *var_len, WriteMethod ** write_method)
{
	struct x400pMIB_data *StorageTmp;

	if (header_generic(vp, name, length, exact, var_len, write_method)
	    == MATCH_FAILED)
		return NULL;

	/* Refresh the MIB values if required. */
	refresh_x400pMIB();

	if ((StorageTmp = x400pMIBStorage) == NULL)
		return NULL;

	/* This is where we do the value assignments for the mib results. */

	switch (vp->magic) {

	case X400PCARDNEXTINDEX:
		*write_method = NULL;	/* read-only */
		*var_len = sizeof(StorageTmp->x400pCardNextIndex);
		return (u_char *) &StorageTmp->x400pCardNextIndex;

	default:
		ERROR_MSG("");
	}
	return NULL;
}

